import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import * as api from '../services/api';

class CategoriesList extends Component {
  constructor() {
    super();
    this.state = {
      category: [],
      productList: [],
      received: false,
    };
  }

  componentDidMount() {
    this.fetchCategory();
  }

  async fetchCategory() {
    const category = await api.getCategories();
    this.setState({ category });
  }

  async filterCategory({ target }) {
    const { value } = target;
    const categoryId='';
    api.getProductsFromCategoryAndQuery(categoryId, value)
      .then((result) => this.setState({ productList: result.results, received: true }));
  }

  render() {
    const { category, productList, received } = this.state;
    if (received) {
      return (
        <ul>
          {productList.length
            ? productList.map(({ id, title, thumbnail, price }) => (
              <li
                key={ id }
                data-testid="product"
              >
                <h3>{ title }</h3>
                <img src={ thumbnail } alt="Product" />
                <p>{ price }</p>
              </li>
            )) : (<li> Nenhum produto foi encontrado </li>)}
        </ul>
      );
    }
    return (
      <div className="category-list">
        {category.map((item) => (
          <label htmlFor={ item.id } key={ item.id } className="category-item">
            {item.name}
            <input
              type="radio"
              data-testid="category"
              name="categoryId"
              value={ item.id }
              onClick={ this.filterCategory() }
            />
          </label>
        ))}
      </div>
    );
  }
}

export default CategoriesList;
